	EndPoint->x = 0;
	EndPoint->y = 0;

	for ( ULONG32 k = 0; Buffer[ k ]; k++ ) {

		CHAR Glyph = ( CHAR )Buffer[ k ];
		switch ( Glyph ) {
		case '\n':
			EndPoint->y += 16;
		case '\r':
			EndPoint->x = 0;
			break;
		case '\t':
			EndPoint->x += ( 8 * 3 ) + ( 4 - ( EndPoint->x % 4 ) );
			break;
		default:
			EndPoint->x += 8;
			break;
		}

		if ( EndPoint->x >= Region->x ) {

			EndPoint->x = 0;
			EndPoint->y += 16;
		}

		if ( EndPoint->y >= Region->y ) {

			return;
		}
	}

